import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { useLanguage } from '../context/LanguageContext';
import './Navbar.css';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const { t, language, toggleLanguage, isRTL } = useLanguage();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="navbar" dir={isRTL ? 'rtl' : 'ltr'}>
      <div className="container">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            <div className="brand-logo">
              <div className="logo-icon-wrapper">
                <svg className="car-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M5 11L6.5 6.5H17.5L19 11M5 11H3M5 11V16M19 11H21M19 11V16M7 16H7.01M17 16H17.01M7 16C7.55228 16 8 15.5523 8 15C8 14.4477 7.55228 14 7 14C6.44772 14 6 14.4477 6 15C6 15.5523 6.44772 16 7 16ZM17 16C17.5523 16 18 15.5523 18 15C18 14.4477 17.5523 14 17 14C16.4477 14 16 14.4477 16 15C16 15.5523 16.4477 16 17 16Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </div>
              <div className="brand-text">
                <span className="logo-text">OFFROAD</span>
                <span className="logo-subtext">RENTALHUB</span>
              </div>
            </div>
          </Link>
          <div className="navbar-links">
            <Link to="/">{t('nav.home')}</Link>
            <Link to="/about">{t('nav.about')}</Link>
            <Link to="/destination">{t('nav.destination')}</Link>
            <Link to="/service">{t('nav.service')}</Link>
            <Link to="/contact">{t('nav.contact')}</Link>
            <Link to="/" className="btn btn-book-now">
              {t('nav.bookNow')} {isRTL ? '‚Üê' : '‚Üí'}
            </Link>
            <button 
              className="language-toggle" 
              onClick={toggleLanguage}
              title={language === 'en' ? 'Switch to Arabic' : 'ÿßŸÑÿ™ÿ®ÿØŸäŸÑ ÿ•ŸÑŸâ ÿßŸÑÿ•ŸÜÿ¨ŸÑŸäÿ≤Ÿäÿ©'}
            >
              {language === 'en' ? 'üá¶üá™' : 'üá¨üáß'}
            </button>
            {/* {user && (
              <>
                <Link to="/my-bookings" className="user-link">My Bookings</Link>
                {user.role === 'admin' && (
                  <Link to="/admin" className="user-link">Admin</Link>
                )}
                <button onClick={handleLogout} className="btn-logout">
                  Logout
                </button>
              </>
            )}
            {!user && (
              <>
                <Link to="/login" className="user-link">Sign In</Link>
                <Link to="/signup" className="user-link">Register</Link>
              </>
            )} */}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
