import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './BookingForm.css';

const BookingForm = () => {
  const { carId } = useParams();
  const navigate = useNavigate();
  const [car, setCar] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const [formData, setFormData] = useState({
    startDate: '',
    startTime: '',
    duration: '60min',
    numberOfRiders: 1,
    pickupLocation: '',
    contactNumber: '',
    name: '',
    email: ''
  });

  useEffect(() => {
    fetchCar();
  }, [carId]);

  const fetchCar = async () => {
    try {
      const response = await axios.get(`/api/cars/${carId}`);
      setCar(response.data);
    } catch (error) {
      console.error('Error fetching car:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const calculateTotal = () => {
    if (!formData.startDate || !formData.endDate || !car) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const days = Math.ceil((end - start) / (1000 * 60 * 60 * 24));
    return days > 0 ? days * car.pricePerDay : 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSubmitting(true);

    try {
      const response = await axios.post('/api/bookings', {
        carId,
        ...formData
      });

      setSuccess(true);
      setTimeout(() => {
        navigate('/my-bookings');
      }, 2000);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to create booking');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!car) {
    return <div className="container">Car not found</div>;
  }

  const totalDays = formData.startDate && formData.endDate
    ? Math.ceil((new Date(formData.endDate) - new Date(formData.startDate)) / (1000 * 60 * 60 * 24))
    : 0;

  return (
    <div className="booking-form-page">
      <div className="container">
        <h1>Book Your Car</h1>
        {success && (
          <div className="alert alert-success">
            Booking created successfully! Redirecting to your bookings...
          </div>
        )}
        {error && <div className="alert alert-error">{error}</div>}

        <div className="booking-content">
          <div className="car-summary">
            <img src={car.image} alt={car.name} />
            <h3>{car.name}</h3>
            <p>{car.brand} {car.model} ({car.year})</p>
            <p className="price">${car.pricePerDay} / day</p>
          </div>

          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-group">
              <label>Start Date *</label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleChange}
                min={new Date().toISOString().split('T')[0]}
                required
              />
            </div>

            <div className="form-group">
              <label>End Date *</label>
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleChange}
                min={formData.startDate || new Date().toISOString().split('T')[0]}
                required
              />
            </div>

            <div className="form-group">
              <label>Pickup Location</label>
              <input
                type="text"
                name="pickupLocation"
                value={formData.pickupLocation}
                onChange={handleChange}
                placeholder="Enter pickup location"
              />
            </div>

            <div className="form-group">
              <label>Contact Number</label>
              <input
                type="tel"
                name="contactNumber"
                value={formData.contactNumber}
                onChange={handleChange}
                placeholder="Enter your contact number"
              />
            </div>

            {totalDays > 0 && (
              <div className="booking-summary">
                <div className="summary-row">
                  <span>Total Days:</span>
                  <span>{totalDays}</span>
                </div>
                <div className="summary-row">
                  <span>Price per Day:</span>
                  <span>${car.pricePerDay}</span>
                </div>
                <div className="summary-row total">
                  <span>Total Price:</span>
                  <span>${calculateTotal()}</span>
                </div>
              </div>
            )}

            <button
              type="submit"
              className="btn btn-primary btn-large"
              disabled={submitting || !formData.startDate || !formData.endDate}
            >
              {submitting ? 'Booking...' : 'Confirm Booking'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BookingForm;
