import React, { useState } from 'react';
import './Gallery.css';

const Gallery = () => {
  const galleryImages = [
    {
      id: 1,
      title: 'Desert Adventure',
      image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=600&fit=crop',
      category: 'Buggies'
    },
    {
      id: 2,
      title: 'Polaris RZR Action',
      image: 'https://images.unsplash.com/photo-1519682337058-a94d519337bc?w=800&h=600&fit=crop',
      category: 'Buggies'
    },
    {
      id: 3,
      title: 'Can-Am Maverick',
      image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=600&fit=crop',
      category: 'Buggies'
    },
    {
      id: 4,
      title: 'Desert Dunes',
      image: 'https://images.unsplash.com/photo-1519682337058-a94d519337bc?w=800&h=600&fit=crop',
      category: 'Landscape'
    },
    {
      id: 5,
      title: 'ATV Adventure',
      image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=600&fit=crop',
      category: 'ATVs'
    },
    {
      id: 6,
      title: 'Sunset Ride',
      image: 'https://images.unsplash.com/photo-1519682337058-a94d519337bc?w=800&h=600&fit=crop',
      category: 'Buggies'
    },
    {
      id: 7,
      title: 'Dirt Bike',
      image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=600&fit=crop',
      category: 'Dirt Bikes'
    },
    {
      id: 8,
      title: 'Desert Safari',
      image: 'https://images.unsplash.com/photo-1519682337058-a94d519337bc?w=800&h=600&fit=crop',
      category: 'Tours'
    },
    {
      id: 9,
      title: 'Quad Bike Fun',
      image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=600&fit=crop',
      category: 'ATVs'
    }
  ];

  const [selectedCategory, setSelectedCategory] = useState('All');
  const categories = ['All', 'Buggies', 'ATVs', 'Dirt Bikes', 'Tours', 'Landscape'];

  const filteredImages = selectedCategory === 'All' 
    ? galleryImages 
    : galleryImages.filter(img => img.category === selectedCategory);

  return (
    <div className="gallery-page">
      <div className="hero-section">
        <div className="hero-overlay">
          <div className="container">
            <div className="hero-content">
              <h1>Gallery</h1>
              <p>Home â†’ Gallery</p>
            </div>
          </div>
        </div>
      </div>

      <div className="gallery-content">
        <div className="container">
          <div className="gallery-intro">
            <span className="section-label">Our Adventures</span>
            <h2>Photo Gallery & Videos</h2>
            <p>Experience the thrill through our collection of adventure moments</p>
          </div>

          <div className="gallery-filters">
            {categories.map(category => (
              <button
                key={category}
                className={selectedCategory === category ? 'active' : ''}
                onClick={() => setSelectedCategory(category)}
              >
                {category}
              </button>
            ))}
          </div>

          <div className="gallery-grid">
            {filteredImages.map((item) => (
              <div key={item.id} className="gallery-item">
                <img src={item.image} alt={item.title} />
                <div className="gallery-overlay">
                  <h3>{item.title}</h3>
                  <span className="gallery-category">{item.category}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Gallery;
