import React, { useState, useEffect } from 'react';
import emailjs from '@emailjs/browser';
import axios from 'axios';
import HeroCarousel from '../components/HeroCarousel';
import { useLanguage } from '../context/LanguageContext';
import './Contact.css';

const Contact = () => {
  const { t } = useLanguage();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });
  const [loading, setLoading] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  // Initialize EmailJS
  useEffect(() => {
    const publicKey = process.env.REACT_APP_EMAILJS_PUBLIC_KEY;
    if (publicKey && publicKey !== 'YOUR_PUBLIC_KEY' && publicKey !== 'your_public_key') {
      emailjs.init(publicKey);
    }
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSubmitStatus(null);

    try {
      // EmailJS configuration
      const serviceID = process.env.REACT_APP_EMAILJS_SERVICE_ID;
      const templateID = process.env.REACT_APP_EMAILJS_TEMPLATE_ID;
      const publicKey = process.env.REACT_APP_EMAILJS_PUBLIC_KEY;

      // Try EmailJS first if configured
      if (serviceID && templateID && publicKey && 
          serviceID !== 'your_service_id' && 
          serviceID !== 'YOUR_SERVICE_ID' &&
          templateID !== 'your_template_id' && 
          templateID !== 'YOUR_TEMPLATE_ID' &&
          publicKey !== 'your_public_key' && 
          publicKey !== 'YOUR_PUBLIC_KEY') {

        // EmailJS template parameters - email will go to m.kamil.se@gmail.com
        const templateParams = {
          from_name: formData.name,
          from_email: formData.email,
          subject: formData.subject,
          message: formData.message,
          to_email: 'm.kamil.se@gmail.com',
          reply_to: formData.email
        };

        // Send email using EmailJS
        try {
          await emailjs.send(serviceID, templateID, templateParams, publicKey);

          const successMessage = 'Aapke email sent ho gaya hai! We will get back to you soon.';
          setSubmitStatus({ type: 'success', message: successMessage });
          setFormData({ name: '', email: '', subject: '', message: '' });

          // Show success alert
          setTimeout(() => {
            alert(successMessage);
          }, 100);

          return;
        } catch (emailjsError) {
          console.error('âŒ EmailJS error:', emailjsError);
          console.log('ðŸ“§ Falling back to backend API...');
        }
      }

      // Fallback to backend API
      console.log('ðŸ“§ Using backend API to send email...');
      const response = await axios.post('/api/contact', {
        ...formData,
        toEmail: 'm.kamil.se@gmail.com'
      });

      if (response.data.success) {
        let successMessage = 'Aapke email sent ho gaya hai! We will get back to you soon.';

        // If email was not actually sent, inform user
        if (response.data.emailSent === false) {
          successMessage = 'Message received! (Email configuration needed - please check server console for setup instructions)';
        }

        setSubmitStatus({ type: 'success', message: successMessage });
        setFormData({ name: '', email: '', subject: '', message: '' });
        setTimeout(() => {
          alert(successMessage);
        }, 100);
      } else {
        throw new Error(response.data.message || 'Error sending message');
      }
    } catch (error) {
      console.error('Contact form error:', error);
      const errorMessage = error.response?.data?.message || 
                          error.text || 
                          error.message || 
                          'Error sending message. Please try again.';
      setSubmitStatus({ 
        type: 'error', 
        message: errorMessage
      });
      setTimeout(() => {
        alert(errorMessage);
      }, 100);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="contact-page">
      <HeroCarousel title={t('contact.title')} subtitle={t('contact.subtitle')} />

      <div className="contact-content">
        <div className="container">
          <div className="contact-main">
            <div className="contact-info">
              <div className="info-section">
                <h3>{t('contact.getInTouch')}</h3>
                <h2>{t('contact.contactInfo')}</h2>
              </div>

              <div className="contact-details">
                <div className="contact-item">
                  <div className="contact-icon">
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M21 10C21 17 12 23 12 23C12 23 3 17 3 10C3 7.61305 3.94821 5.32387 5.63604 3.63604C7.32387 1.94821 9.61305 1 12 1C14.3869 1 16.6761 1.94821 18.364 3.63604C20.0518 5.32387 21 7.61305 21 10Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="M12 13C13.6569 13 15 11.6569 15 10C15 8.34315 13.6569 7 12 7C10.3431 7 9 8.34315 9 10C9 11.6569 10.3431 13 12 13Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  </div>
                  <div className="contact-text">
                    <h4>{t('contact.ourAddress')}</h4>
                    <p>Al badayer desert Hatta Oman Road sharjah</p>
                  </div>
                </div>

                <div className="contact-item">
                  <div className="contact-icon">
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M22 16.92V19.92C22.0011 20.1985 21.9441 20.4742 21.8325 20.7292C21.7209 20.9842 21.5573 21.213 21.3528 21.4007C21.1482 21.5884 20.9071 21.7309 20.6447 21.8187C20.3822 21.9065 20.1042 21.9377 19.83 21.91C16.7428 21.5866 13.787 20.5341 11.19 18.85C8.77382 17.3147 6.72533 15.2662 5.19 12.85C3.49997 10.2412 2.44824 7.27099 2.13 4.18C2.10231 3.90557 2.13359 3.62768 2.22161 3.36537C2.30964 3.10305 2.45226 2.86219 2.6402 2.658C2.82814 2.45381 3.05718 2.29061 3.31231 2.17949C3.56744 2.06838 3.84326 2.01172 4.12 2.013H7.12C7.68147 1.99599 8.23552 2.16789 8.68999 2.49989C9.14446 2.83189 9.4737 3.30611 9.62 3.85L10.9 8.21C11.0442 8.75232 11.0045 9.33369 10.7877 9.85015C10.5709 10.3666 10.1899 10.7883 9.71 11.05L8.09 12C9.51437 14.4133 11.5867 16.4856 14 17.91L14.96 16.29C15.2193 15.8108 15.639 15.431 16.1531 15.2151C16.6672 14.9992 17.2462 14.9593 17.79 15.1L22.15 16.38C22.6939 16.5263 23.1681 16.8555 23.5001 17.31C23.8321 17.7645 24.004 18.3185 23.987 18.88L22 16.92Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  </div>
                  <div className="contact-text">
                    <h4>{t('contact.phoneNumber')}</h4>
                    <p>+971564455568</p>
                  </div>
                </div>

                <div className="contact-item">
                  <div className="contact-icon">
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M4 4H20C21.1 4 22 4.9 22 6V18C22 19.1 21.1 20 20 20H4C2.9 20 2 19.1 2 18V6C2 4.9 2.9 4 4 4Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="L22 6L12 13L2 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  </div>
                  <div className="contact-text">
                    <h4>{t('contact.emailAddress')}</h4>
                    <p>malinfo00@tourm.com</p>
                    <p>supuurt24@tourm.com</p>
                  </div>
                </div>
              </div>
            </div>

            <div className="contact-form-section">
              <h3>{t('contact.sendUsMessage') || 'Send Us A Message'}</h3>
              {submitStatus && (
                <div className={`submit-message ${submitStatus.type}`}>
                  {submitStatus.type === 'success' && (
                    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" style={{width: '20px', height: '20px', marginRight: '10px'}}>
                      <path d="M20 6L9 17L4 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  )}
                  {submitStatus.type === 'error' && (
                    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" style={{width: '20px', height: '20px', marginRight: '10px'}}>
                      <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/>
                      <line x1="12" y1="8" x2="12" y2="12" stroke="currentColor" strokeWidth="2"/>
                      <line x1="12" y1="16" x2="12.01" y2="16" stroke="currentColor" strokeWidth="2"/>
                    </svg>
                  )}
                  {submitStatus.message}
                </div>
              )}
              <form onSubmit={handleSubmit} className="contact-form">
                <div className="form-group">
                  <input
                    type="text"
                    name="name"
                    placeholder={t('contact.yourName')}
                    value={formData.name}
                    onChange={handleChange}
                    required
                    disabled={loading}
                  />
                </div>
                <div className="form-group">
                  <input
                    type="email"
                    name="email"
                    placeholder={t('contact.yourEmail')}
                    value={formData.email}
                    onChange={handleChange}
                    required
                    disabled={loading}
                  />
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    name="subject"
                    placeholder={t('contact.subject')}
                    value={formData.subject}
                    onChange={handleChange}
                    required
                    disabled={loading}
                  />
                </div>
                <div className="form-group">
                  <textarea
                    name="message"
                    placeholder={t('contact.yourMessage')}
                    rows="6"
                    value={formData.message}
                    onChange={handleChange}
                    required
                    disabled={loading}
                  />
                </div>
                <button type="submit" className="btn btn-primary btn-large" disabled={loading}>
                  {loading ? t('contact.sending') || 'Sending...' : t('contact.sendMessage')}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
