// Main Booking Form Component
const BookingForm = () => {
  const [submitting, setSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    pickupLocation: '',
    contactNumber: '',
    customerEmail: '',
    customerName: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const calculateTotal = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const days = Math.ceil((end.getTime() - start.getTime()) / (1000 * 60 * 60 * 24));
    return days > 0 ? days * mockCar.pricePerDay : 0;
  };

  const totalDays = formData.startDate && formData.endDate
    ? Math.ceil((new Date(formData.endDate).getTime() - new Date(formData.startDate).getTime()) / (1000 * 60 * 60 * 24))
    : 0;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 2000));

    setSubmitting(false);
    setShowSuccess(true);

    // Log booking details
    console.log('ðŸŽ‰ Booking Created!', {
      car: mockCar,
      ...formData,
      totalDays,
      totalPrice: calculateTotal()
    });
  };

  const handleCloseSuccess = () => {
    setShowSuccess(false);
    // Reset form
    setFormData({
      startDate: '',
      endDate: '',
      pickupLocation: '',
      contactNumber: '',
      customerEmail: '',
      customerName: ''
    });
  };

  return (
    <div className="bg-gradient-main">
      <div className="container" style={{ paddingTop: '32px', paddingBottom: '48px' }}>
        {/* Header */}
        <div className="text-center space-y-3" style={{ marginBottom: '48px' }}>
          <h1 className="text-gradient" style={{ fontSize: '48px', fontWeight: 'bold', marginBottom: '12px' }}>
            Book Your Dream Car
          </h1>
          <p style={{ fontSize: '18px', color: '#6b7280' }}>
            Complete your booking details below for a premium experience
          </p>
        </div>

        <div className="grid-lg-2" style={{ maxWidth: '1280px', margin: '0 auto' }}>
          {/* Car Summary Card */}
          <div className="card overflow-hidden transition-shadow" style={{ padding: '0' }}>
            <div className="relative overflow-hidden" style={{ height: '320px' }}>
              <img 
                src={mockCar.image} 
                alt={mockCar.name}
                className="w-full h-full object-cover"
              />
              <div className="absolute inset-0" style={{ background: 'linear-gradient(to top, rgba(0,0,0,0.6), rgba(0,0,0,0.2), transparent)' }} />
              <div className="absolute" style={{ bottom: '0', left: '0', right: '0', padding: '24px', color: 'white' }}>
                <h2 style={{ fontSize: '28px', fontWeight: 'bold', marginBottom: '8px' }}>{mockCar.name}</h2>
                <p style={{ color: 'rgba(255, 255, 255, 0.9)' }}>
                  {mockCar.brand} {mockCar.model} ({mockCar.year})
                </p>
              </div>
            </div>

            <div className="space-y-6" style={{ padding: '24px' }}>
              {/* Specifications */}
              <div className="grid-md-3">
                <div className="flex flex-col items-center spec-card-indigo rounded-xl" style={{ padding: '16px' }}>
                  <Users style={{ width: '24px', height: '24px', color: '#6366f1', marginBottom: '8px' }} />
                  <span style={{ fontSize: '14px', color: '#6b7280' }}>Seats</span>
                  <span style={{ fontWeight: '600', color: '#111827' }}>{mockCar.seats}</span>
                </div>
                <div className="flex flex-col items-center spec-card-purple rounded-xl" style={{ padding: '16px' }}>
                  <Settings style={{ width: '24px', height: '24px', color: '#8b5cf6', marginBottom: '8px' }} />
                  <span style={{ fontSize: '14px', color: '#6b7280', textAlign: 'center' }}>Trans.</span>
                  <span style={{ fontWeight: '600', color: '#111827', fontSize: '12px' }}>{mockCar.transmission}</span>
                </div>
                <div className="flex flex-col items-center spec-card-pink rounded-xl" style={{ padding: '16px' }}>
                  <Fuel style={{ width: '24px', height: '24px', color: '#ec4899', marginBottom: '8px' }} />
                  <span style={{ fontSize: '14px', color: '#6b7280' }}>Fuel</span>
                  <span style={{ fontWeight: '600', color: '#111827' }}>{mockCar.fuelType}</span>
                </div>
              </div>

              {/* Price */}
              <div className="bg-gradient-header rounded-xl text-center" style={{ padding: '24px', color: 'white' }}>
                <p style={{ fontSize: '14px', opacity: 0.9, marginBottom: '4px' }}>Daily Rate</p>
                <p style={{ fontSize: '36px', fontWeight: 'bold' }}>
                  {mockCar.pricePerDay} <span style={{ fontSize: '20px' }}>{mockCar.currency}</span>
                </p>
                <p style={{ fontSize: '14px', opacity: 0.9, marginTop: '4px' }}>per day</p>
              </div>
            </div>
          </div>

          {/* Booking Form */}
          <div className="card" style={{ padding: '32px' }}>
            <h3 style={{ fontSize: '24px', fontWeight: 'bold', color: '#111827', marginBottom: '24px' }}>
              Booking Details
            </h3>

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Date Selection */}
              <div className="grid-md-2">
                <div className="space-y-2">
                  <label htmlFor="startDate" className="flex items-center gap-2" style={{ color: '#374151', fontWeight: '500', fontSize: '14px' }}>
                    <Calendar style={{ width: '16px', height: '16px', color: '#6366f1' }} />
                    Start Date *
                  </label>
                  <input
                    type="date"
                    id="startDate"
                    name="startDate"
                    value={formData.startDate}
                    onChange={handleChange}
                    min={new Date().toISOString().split('T')[0]}
                    required
                    className="form-input"
                  />
                </div>

                <div className="space-y-2">
                  <label htmlFor="endDate" className="flex items-center gap-2" style={{ color: '#374151', fontWeight: '500', fontSize: '14px' }}>
                    <Calendar style={{ width: '16px', height: '16px', color: '#8b5cf6' }} />
                    End Date *
                  </label>
                  <input
                    type="date"
                    id="endDate"
                    name="endDate"
                    value={formData.endDate}
                    onChange={handleChange}
                    min={formData.startDate || new Date().toISOString().split('T')[0]}
                    required
                    className="form-input form-input-purple"
                  />
                </div>
              </div>

              {/* Location */}
              <div className="space-y-2">
                <label htmlFor="pickupLocation" className="flex items-center gap-2" style={{ color: '#374151', fontWeight: '500', fontSize: '14px' }}>
                  <MapPin style={{ width: '16px', height: '16px', color: '#ec4899' }} />
                  Pickup Location
                </label>
                <input
                  type="text"
                  id="pickupLocation"
                  name="pickupLocation"
                  value={formData.pickupLocation}
                  onChange={handleChange}
                  placeholder="Enter pickup location"
                  className="form-input form-input-pink"
                />
              </div>

              {/* Contact Information */}
              <div className="space-y-2">
                <label htmlFor="customerName" className="flex items-center gap-2" style={{ color: '#374151', fontWeight: '500', fontSize: '14px' }}>
                  <User style={{ width: '16px', height: '16px', color: '#6366f1' }} />
                  Your Name
                </label>
                <input
                  type="text"
                  id="customerName"
                  name="customerName"
                  value={formData.customerName}
                  onChange={handleChange}
                  placeholder="Enter your full name"
                  className="form-input"
                />
              </div>

              <div className="grid-md-2">
                <div className="space-y-2">
                  <label htmlFor="customerEmail" className="flex items-center gap-2" style={{ color: '#374151', fontWeight: '500', fontSize: '14px' }}>
                    <Mail style={{ width: '16px', height: '16px', color: '#8b5cf6' }} />
                    Email Address *
                  </label>
                  <input
                    type="email"
                    id="customerEmail"
                    name="customerEmail"
                    value={formData.customerEmail}
                    onChange={handleChange}
                    placeholder="your@email.com"
                    required
                    className="form-input form-input-purple"
                  />
                </div>

                <div className="space-y-2">
                  <label htmlFor="contactNumber" className="flex items-center gap-2" style={{ color: '#374151', fontWeight: '500', fontSize: '14px' }}>
                    <Phone style={{ width: '16px', height: '16px', color: '#ec4899' }} />
                    Contact Number
                  </label>
                  <input
                    type="tel"
                    id="contactNumber"
                    name="contactNumber"
                    value={formData.contactNumber}
                    onChange={handleChange}
                    placeholder="+971 XX XXX XXXX"
                    className="form-input form-input-pink"
                  />
                </div>
              </div>

              {/* Booking Summary */}
              {totalDays > 0 && (
                <div className="rounded-xl" style={{ background: 'linear-gradient(135deg, #eef2ff, #faf5ff, #fdf2f8)', padding: '24px', border: '2px solid #e0e7ff' }}>
                  <h4 style={{ fontWeight: '600', color: '#111827', marginBottom: '16px' }}>Booking Summary</h4>
                  <div className="space-y-3">
                    <div className="flex justify-between" style={{ fontSize: '14px' }}>
                      <span style={{ color: '#6b7280' }}>Total Days:</span>
                      <span style={{ fontWeight: '600', color: '#111827' }}>{totalDays} {totalDays === 1 ? 'day' : 'days'}</span>
                    </div>
                    <div className="flex justify-between" style={{ fontSize: '14px' }}>
                      <span style={{ color: '#6b7280' }}>Price per Day:</span>
                      <span style={{ fontWeight: '600', color: '#111827' }}>{mockCar.pricePerDay} {mockCar.currency}</span>
                    </div>
                    <div style={{ height: '1px', background: 'linear-gradient(90deg, #c7d2fe, #ddd6fe, #fbcfe8)' }}></div>
                    <div className="flex justify-between">
                      <span style={{ fontWeight: '600', color: '#111827' }}>Total Price:</span>
                      <span className="text-gradient" style={{ fontSize: '24px', fontWeight: 'bold' }}>
                        {calculateTotal()} {mockCar.currency}
                      </span>
                    </div>
                  </div>
                </div>
              )}

              {/* Submit Button */}
              <button
                type="submit"
                disabled={submitting || !formData.startDate || !formData.endDate || !formData.customerEmail}
                className="btn-submit"
              >
                {submitting ? (
                  <>
                    <Loader2 className="animate-spin" style={{ width: '20px', height: '20px' }} />
                    Processing Booking...
                  </>
                ) : (
                  <>
                    <Check style={{ width: '20px', height: '20px' }} />
                    Confirm Booking
                  </>
                )}
              </button>
            </form>
          </div>
        </div>
      </div>

      {/* Success Modal */}
      <SuccessModal 
        open={showSuccess} 
        onClose={handleCloseSuccess}
        bookingDetails={{
          carName: mockCar.name,
          totalDays,
          totalPrice: calculateTotal(),
          currency: mockCar.currency,
          customerEmail: formData.customerEmail
        }}
      />
    </div>
  );
};

export default BookingForm;
