import React from 'react';
import { Link } from 'react-router-dom';
import HeroCarousel from '../components/HeroCarousel';
import { useLanguage } from '../context/LanguageContext';
import './Service.css';

const Service = () => {
  const { t } = useLanguage();
  const services = [
    {
      id: 1,
      title: t('service.photoShoot'),
      listings: `2 ${t('service.listing')}`,
      image: '/assets/vehicles/polaris-rzr1000.jpg',
      description: t('service.photoShootDesc')
    },
    {
      id: 2,
      title: t('service.tourGuide'),
      listings: `22 ${t('service.listing')}`,
      image: '/assets/vehicles/canam-maverick-turbo.jpg',
      description: t('service.tourGuideDesc')
    },
    {
      id: 3,
      title: t('service.cozyEvent'),
      listings: `23 ${t('service.listing')}`,
      image: '/assets/vehicles/polaris-rzr1000-4seater.jpg',
      description: t('service.cozyEventDesc')
    },
    {
      id: 4,
      title: t('service.interestingRest'),
      listings: `24 ${t('service.listing')}`,
      image: '/assets/vehicles/dirt-bike.jpg',
      description: t('service.interestingRestDesc')
    }
  ];

  return (
    <div className="service-page">
      <HeroCarousel title={t('service.title')} subtitle={t('service.subtitle')} />

      <div className="service-content">
        <div className="container">
          <div className="services-grid">
            {services.map((service) => (
              <div key={service.id} className="service-card">
                <div className="service-image">
                  <img src={service.image} alt={service.title} />
                </div>
                <div className="service-info">
                  <h3>{service.title}</h3>
                  <p className="service-listings">{service.listings}</p>
                  <p className="service-description">{service.description}</p>
                  <Link to="/destination" className="btn btn-service">
                    Book Now â†’
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Service;
