import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

const Home = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentSlide, setCurrentSlide] = useState(0);

  // Hero background images - vehicle images
  const heroImages = [
    '/assets/vehicles/canam-maverick-turbo.jpg',
    '/assets/vehicles/polaris-rzr1000.jpg',
    '/assets/vehicles/polaris-rzr1000-4seater.jpg',
    '/assets/vehicles/canam-maverick-xrs.jpg',
    '/assets/vehicles/dirt-bike.jpg',
    '/assets/vehicles/canam-maverick-x3.jpg'
  ];

  useEffect(() => {
    fetchCars();
  }, []);

  // Auto-change hero background every 10 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroImages.length);
    }, 10000); // 10 seconds

    return () => clearInterval(interval);
  }, [heroImages.length]);

  const fetchCars = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/cars');
      setCars(response.data.slice(0, 6)); // Show only 6 for home page
    } catch (error) {
      console.error('Error fetching cars:', error);
    } finally {
      setLoading(false);
    }
  };

  const categories = [
    { name: 'ATV Rides', image: '/assets/vehicles/quad-bike-single.jpg' },
    { name: 'UTV Tours', image: '/assets/vehicles/polaris-rzr1000.jpg' },
    { name: 'Dirt Bikes', image: '/assets/vehicles/dirt-bike.jpg' },
    { name: 'Family Tours', image: '/assets/vehicles/polaris-rzr1000-4seater.jpg' },
    { name: 'Premium Rides', image: '/assets/vehicles/canam-maverick-xrs.jpg' }
  ];

  return (
    <div className="home">
      <div className="hero-section">
        {heroImages.map((image, index) => (
          <div
            key={index}
            className={`hero-slide ${index === currentSlide ? 'active' : ''}`}
            style={{
              background: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(${image}) center/cover`
            }}
          />
        ))}
        <div className="hero-overlay">
          <div className="container">
            <div className="hero-content">
              <span className="hero-label">Wonderful Place For You</span>
              <h1>Explore the beautiful place in the world</h1>
              <Link to="/destination" className="btn btn-hero">
                Enter Gallery
              </Link>
            </div>
          </div>
        </div>
        <div className="hero-indicators">
          {heroImages.map((_, index) => (
            <span
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => setCurrentSlide(index)}
            />
          ))}
        </div>
      </div>

      <div className="tour-categories-section">
        <div className="container">
          <div className="section-header">
            <span className="section-label">Tour Categories</span>
          </div>
          <div className="categories-grid">
            {categories.map((category, index) => (
              <div key={index} className="category-card">
                <img src={category.image} alt={category.name} />
                <div className="category-overlay">
                  <h3>{category.name}</h3>
                  <Link to="/destination" className="category-link">See More</Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="featured-section">
        <div className="container">
          <div className="section-header">
            <span className="section-label">Featured</span>
            <h2>Explore the Beauty of Maldives and enjoy</h2>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod 
              tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, 
              quis nostrud exercitation ullamco laboris.
            </p>
          </div>
          {loading ? (
            <div className="loading">Loading...</div>
          ) : (
            <div className="featured-grid">
              {cars.map((car) => (
                <div key={car._id} className="featured-card">
                  <img src={car.image} alt={car.name} />
                  <div className="featured-content">
                    <h3>{car.name}</h3>
                    <p>{car.brand} {car.model}</p>
                    <div className="featured-price">
                      {car.price60min ? (
                        <>
                          <span>{car.price60min} {car.currency || 'AED'}</span>
                          <span>/60min</span>
                        </>
                      ) : (
                        <>
                          <span>{car.pricePerDay} {car.currency || 'AED'}</span>
                          <span>/day</span>
                        </>
                      )}
                    </div>
                    <Link to={`/car/${car._id}`} className="btn btn-primary">
                      Book Now â†’
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
          <div className="view-all">
            <Link to="/destination" className="btn btn-secondary">
              View All
            </Link>
          </div>
        </div>
      </div>

      <div className="gallery-section">
        <div className="container">
          <div className="section-header">
            <span className="section-label">From our gallery</span>
          </div>
          <div className="gallery-grid">
            <div className="gallery-item large">
              <img src="/assets/vehicles/canam-maverick-turbo.jpg" alt="Canam Maverick Turbo" />
            </div>
            <div className="gallery-item">
              <img src="/assets/vehicles/polaris-rzr1000.jpg" alt="Polaris RZR1000" />
            </div>
            <div className="gallery-item">
              <img src="/assets/vehicles/dirt-bike.jpg" alt="Dirt Bike" />
            </div>
            <div className="gallery-item large">
              <img src="/assets/vehicles/polaris-rzr1000-4seater.jpg" alt="Polaris RZR 4 Seater" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
