  const [submitting, setSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    pickupLocation: '',
    contactNumber: '',
    customerEmail: '',
    customerName: ''
  });

  useEffect(() => {
    const fetchCar = async () => {
      try {
        const res = await axios.get(`/api/cars/${carId}`);
        setCar(res.data);
      } catch (err) {
        console.error('Error fetching car:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchCar();
  }, [carId]);

  const handleChange = (e) => setFormData({...formData, [e.target.name]: e.target.value});

  const calculateTotal = () => {
    if (!formData.startDate || !formData.endDate || !car) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const days = Math.ceil((end - start) / (1000 * 60 * 60 * 24));
    return days > 0 ? days * car.pricePerDay : 0;
  };

  const totalDays = formData.startDate && formData.endDate
    ? Math.ceil((new Date(formData.endDate) - new Date(formData.startDate)) / (1000 * 60 * 60 * 24))
    : 0;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const res = await axios.post('/api/bookings', {
        carId: car._id,
        ...formData
      });
      setShowSuccess(true);
    } catch (err) {
      alert(err.response?.data?.message || 'Booking failed');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <div>Loading car...</div>;
  if (!car) return <div>Car not found</div>;

  return (
    <div className="booking-form-page">
      <div className="container">
        <h1>Book {car.name}</h1>
        {/* Form & Summary here (reuse your styled form) */}
