        <form onSubmit={handleSubmit}>
          {/* form fields here */}
        </form>
      </div>

      <SuccessModal 
        open={showSuccess} 
        onClose={() => setShowSuccess(false)} 
        bookingDetails={{
          carName: car.name,
          totalDays,
          totalPrice: calculateTotal(),
          currency: car.currency,
          customerEmail: formData.customerEmail
        }}
      />
    </div>
  );
};

