import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './BookingForm.css';

const BookingForm = () => {
  const { carId } = useParams();
  const navigate = useNavigate();
  const [car, setCar] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    pickupLocation: '',
    contactNumber: '',
    customerEmail: '',
    customerName: ''
  });

  useEffect(() => {
    fetchCar();
  }, [carId]);

  const fetchCar = async () => {
    try {
      const response = await axios.get(`/api/cars/${carId}`);
      setCar(response.data);
    } catch (error) {
      console.error('Error fetching car:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const calculateTotal = () => {
    if (!formData.startDate || !formData.endDate || !car) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const days = Math.ceil((end - start) / (1000 * 60 * 60 * 24));
    // Use price60min if available, otherwise pricePerDay
    const pricePerDay = car.price60min ? car.price60min : car.pricePerDay;
    return days > 0 ? days * pricePerDay : 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSubmitting(true);

    try {
      const response = await axios.post('/api/bookings', {
        carId,
        ...formData
      });

      // WhatsApp message is sent automatically from backend
      // No need to open WhatsApp link from frontend

      setSuccess(true);
      setTimeout(() => {
        navigate('/');
      }, 3000);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to create booking');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!car) {
    return <div className="container">Car not found</div>;
  }

  const totalDays = formData.startDate && formData.endDate
    ? Math.ceil((new Date(formData.endDate) - new Date(formData.startDate)) / (1000 * 60 * 60 * 24))
    : 0;

  const pricePerDay = car.price60min ? car.price60min : car.pricePerDay;
  const currency = car.currency || 'AED';

  return (
    <div className="booking-form-page">
      <div className="container">
        <div className="booking-header">
          <h1>Book Your Vehicle</h1>
          <p className="booking-subtitle">Complete your booking details below</p>
        </div>

        {success && (
          <div className="alert alert-success">
            <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M9 12L11 14L15 10M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            <span>Booking created successfully! You will receive a confirmation email. Redirecting to home...</span>
          </div>
        )}
        {error && (
          <div className="alert alert-error">
            <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 8V12M12 16H12.01M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            <span>{error}</span>
          </div>
        )}

        <div className="booking-content">
          <div className="car-summary-card">
            <div className="car-image-wrapper">
              <img src={car.image} alt={car.name} />
              {!car.available && (
                <div className="unavailable-badge">Not Available</div>
              )}
            </div>
            <div className="car-info">
              <h3>{car.name}</h3>
              <p className="car-details">{car.brand} {car.model} ({car.year})</p>
              <div className="car-specs">
                <span className="spec-item">üë• {car.seats} Seats</span>
                <span className="spec-item">‚öôÔ∏è {car.transmission}</span>
                <span className="spec-item">‚õΩ {car.fuelType}</span>
              </div>
              <div className="price-section">
                <span className="price-label">Price</span>
                <span className="price-amount">{pricePerDay} {currency}</span>
                <span className="price-unit">{car.price60min ? '/60min' : '/day'}</span>
              </div>
            </div>
          </div>

          <div className="booking-form-wrapper">
            <form onSubmit={handleSubmit} className="booking-form">
              <div className="form-section">
                <h3 className="section-title">Booking Details</h3>

                <div className="form-row">
                  <div className="form-group">
                    <label>
                      <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M8 7V3M16 7V3M7 11H17M5 21H19C20.1046 21 21 20.1046 21 19V7C21 5.89543 20.1046 5 19 5H5C3.89543 5 3 5.89543 3 7V19C3 20.1046 3.89543 21 5 21Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      </svg>
                      Start Date *
                    </label>
                    <input
                      type="date"
                      name="startDate"
                      value={formData.startDate}
                      onChange={handleChange}
                      min={new Date().toISOString().split('T')[0]}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>
                      <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M8 7V3M16 7V3M7 11H17M5 21H19C20.1046 21 21 20.1046 21 19V7C21 5.89543 20.1046 5 19 5H5C3.89543 5 3 5.89543 3 7V19C3 20.1046 3.89543 21 5 21Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      </svg>
                      End Date *
                    </label>
                    <input
                      type="date"
                      name="endDate"
                      value={formData.endDate}
                      onChange={handleChange}
                      min={formData.startDate || new Date().toISOString().split('T')[0]}
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label>
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M21 10C21 17 12 23 12 23C12 23 3 17 3 10C3 7.61305 3.94821 5.32387 5.63604 3.63604C7.32387 1.94821 9.61305 1 12 1C14.3869 1 16.6761 1.94821 18.364 3.63604C20.0518 5.32387 21 7.61305 21 10Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="M12 13C13.6569 13 15 11.6569 15 10C15 8.34315 13.6569 7 12 7C10.3431 7 9 8.34315 9 10C9 11.6569 10.3431 13 12 13Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                    Pickup Location
                  </label>
                  <input
                    type="text"
                    name="pickupLocation"
                    value={formData.pickupLocation}
                    onChange={handleChange}
                    placeholder="Enter pickup location"
                  />
                </div>

                <div className="form-group">
                  <label>
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M22 16.92V19.92C22.0011 20.1985 21.9441 20.4742 21.8325 20.7292C21.7209 20.9842 21.5573 21.213 21.3528 21.4007C21.1482 21.5884 20.9071 21.7309 20.6447 21.8187C20.3822 21.9065 20.1042 21.9377 19.83 21.91C16.7428 21.5866 13.787 20.5341 11.19 18.85C8.77382 17.3147 6.72533 15.2662 5.19 12.85C3.49997 10.2412 2.44824 7.27099 2.13 4.18C2.10231 3.90557 2.13359 3.62768 2.22161 3.36537C2.30964 3.10305 2.45226 2.86219 2.6402 2.658C2.82814 2.45381 3.05718 2.29061 3.31231 2.17949C3.56744 2.06838 3.84326 2.01172 4.12 2.013H7.12C7.68147 1.99599 8.23552 2.16789 8.68999 2.49989C9.14446 2.83189 9.4737 3.30611 9.62 3.85L10.9 8.21C11.0442 8.75232 11.0045 9.33369 10.7877 9.85015C10.5709 10.3666 10.1899 10.7883 9.71 11.05L8.09 12C9.51437 14.4133 11.5867 16.4856 14 17.91L14.96 16.29C15.2193 15.8108 15.639 15.431 16.1531 15.2151C16.6672 14.9992 17.2462 14.9593 17.79 15.1L22.15 16.38C22.6939 16.5263 23.1681 16.8555 23.5001 17.31C23.8321 17.7645 24.004 18.3185 23.987 18.88L22 16.92Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                    Contact Number
                  </label>
                  <input
                    type="tel"
                    name="contactNumber"
                    value={formData.contactNumber}
                    onChange={handleChange}
                    placeholder="Enter your contact number"
                  />
                </div>

                <div className="form-group">
                  <label>
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M4 4H20C21.1046 4 22 4.89543 22 6V18C22 19.1046 21.1046 20 20 20H4C2.89543 20 2 19.1046 2 18V6C2 4.89543 2.89543 4 4 4Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="M22 6L12 13L2 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                    Email Address *
                  </label>
                  <input
                    type="email"
                    name="customerEmail"
                    value={formData.customerEmail}
                    onChange={handleChange}
                    placeholder="Enter your email address"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M16 7C16 9.20914 14.2091 11 12 11C9.79086 11 8 9.20914 8 7C8 4.79086 9.79086 3 12 3C14.2091 3 16 4.79086 16 7Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="M12 14C8.13401 14 5 17.134 5 21H19C19 17.134 15.866 14 12 14Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                    Your Name
                  </label>
                  <input
                    type="text"
                    name="customerName"
                    value={formData.customerName}
                    onChange={handleChange}
                    placeholder="Enter your name (optional)"
                  />
                </div>
              </div>

              {totalDays > 0 && (
                <div className="booking-summary-card">
                  <h3 className="summary-title">Booking Summary</h3>
                  <div className="summary-content">
                    <div className="summary-row">
                      <span className="summary-label">Total Days:</span>
                      <span className="summary-value">{totalDays} {totalDays === 1 ? 'day' : 'days'}</span>
                    </div>
                    <div className="summary-row">
                      <span className="summary-label">Price per {car.price60min ? '60min' : 'Day'}:</span>
                      <span className="summary-value">{pricePerDay} {currency}</span>
                    </div>
                    <div className="summary-divider"></div>
                    <div className="summary-row total">
                      <span className="summary-label">Total Price:</span>
                      <span className="summary-value">{calculateTotal()} {currency}</span>
                    </div>
                  </div>
                </div>
              )}

              <button
                type="submit"
                className="btn btn-confirm"
                disabled={submitting || !formData.startDate || !formData.endDate || !formData.customerEmail}
              >
                {submitting ? (
                  <>
                    <span className="spinner"></span>
                    Processing...
                  </>
                ) : (
                  <>
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M9 12L11 14L15 10M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                    Confirm Booking
                  </>
                )}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingForm;
