import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { useLanguage } from '../context/LanguageContext';
import './Navbar.css';
import Logo from '../assets/Logo5.png'

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const { t, language, toggleLanguage, isRTL } = useLanguage();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="navbar-content">
          <div className="">
            <img
              src={Logo}
              alt="logo"
              style={{ width: "100px", height: "80px" }}
            />
          </div>
          <div className="navbar-links">
            <Link to="/">{t('nav.home')}</Link>
            <Link to="/about">{t('nav.about')}</Link>
            <Link to="/destination">{t('nav.destination')}</Link>
            <Link to="/service">{t('nav.service')}</Link>
            <Link to="/contact">{t('nav.contact')}</Link>
            <button
              className="language-toggle"
              onClick={toggleLanguage}
              title={language === 'en' ? 'Switch to Arabic' : 'ÿßŸÑÿ™ÿ®ÿØŸäŸÑ ÿ•ŸÑŸâ ÿßŸÑÿ•ŸÜÿ¨ŸÑŸäÿ≤Ÿäÿ©'}
            >
              <span className="flag-icon">{language === 'en' ? 'üá¶üá™' : 'üá¨üáß'}</span>
              <span className="language-text">{language === 'en' ? 'Arabic' : 'English'}</span>
            </button>
            {/* Admin Login Button - Only show when no user is logged in */}
            {!user && (
              <Link to="/login" className="admin-login-link">
                {t('nav.adminLogin') || 'Admin Login'}
              </Link>
            )}
            {/* Admin Dashboard and Logout - Only show when admin is logged in */}
            {user && user.role === 'admin' && (
              <>
                <Link to="/admin" className="admin-dashboard-link">
                  {t('nav.admin') || 'Admin Dashboard'}
                </Link>
                <button onClick={handleLogout} className="btn-logout">
                  {t('nav.logout')}
                </button>
              </>
            )}
            {/* Regular users don't see login/logout - they can book without login */}
            {/* <Link to="/" className="btn btn-book-now">
              {t('nav.bookNow')} {isRTL ? '‚Üê' : '‚Üí'}
            </Link> */}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
