import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './Navbar.css';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            üèúÔ∏è Dejavu Adventure
          </Link>
          <div className="navbar-links">
            <Link to="/">Home</Link>
            <Link to="/about">About Us</Link>
            <Link to="/destination">Destination</Link>
            <Link to="/service">Service</Link>
            <Link to="/activities">Activities</Link>
            <Link to="/blog">Blog</Link>
            <Link to="/contact">Contact Us</Link>
            <Link to="/" className="btn btn-book-now">
              Book Now ‚Üí
            </Link>
            {user && (
              <>
                <Link to="/my-bookings" className="user-link">My Bookings</Link>
                {user.role === 'admin' && (
                  <Link to="/admin" className="user-link">Admin</Link>
                )}
                <button onClick={handleLogout} className="btn-logout">
                  Logout
                </button>
              </>
            )}
            {!user && (
              <>
                <Link to="/login" className="user-link">Sign In</Link>
                <Link to="/signup" className="user-link">Register</Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
