import React from 'react';
import './Safety.css';

const Safety = () => {
  const safetyRules = [
    {
      id: 1,
      title: 'Age Requirements',
      description: 'Minimum age 18 years for driving. Passengers must be at least 5 years old. Valid driving license required for drivers.'
    },
    {
      id: 2,
      title: 'Safety Equipment',
      description: 'Helmets and safety gear are mandatory and provided free of charge. All participants must wear protective equipment at all times.'
    },
    {
      id: 3,
      title: 'Health & Fitness',
      description: 'Participants should be in good physical condition. Not recommended for pregnant women or people with heart conditions.'
    },
    {
      id: 4,
      title: 'Waiver & Insurance',
      description: 'All participants must sign a liability waiver before the ride. Basic insurance is included in the rental package.'
    },
    {
      id: 5,
      title: 'Driving Rules',
      description: 'Follow guide instructions at all times. Stay within designated areas. No alcohol consumption before or during the ride.'
    },
    {
      id: 6,
      title: 'Weather Conditions',
      description: 'Rides may be cancelled or rescheduled due to extreme weather conditions. Full refund or rescheduling available.'
    }
  ];

  return (
    <div className="safety-page">
      <div className="hero-section">
        <div className="hero-overlay">
          <div className="container">
            <div className="hero-content">
              <h1>Safety Rules & Requirements</h1>
              <p>Your safety is our priority</p>
            </div>
          </div>
        </div>
      </div>

      <div className="safety-content">
        <div className="container">
          <div className="safety-intro">
            <h2>Important Safety Information</h2>
            <p>
              At Dejavu Adventure, your safety is our top priority. Please read and understand 
              all safety rules and requirements before booking your adventure.
            </p>
          </div>

          <div className="safety-rules-grid">
            {safetyRules.map((rule) => (
              <div key={rule.id} className="safety-card">
                <div className="safety-icon">üõ°Ô∏è</div>
                <h3>{rule.title}</h3>
                <p>{rule.description}</p>
              </div>
            ))}
          </div>

          <div className="safety-important">
            <h3>‚ö†Ô∏è Important Notes</h3>
            <ul>
              <li>All participants must arrive 30 minutes before scheduled time for briefing</li>
              <li>Bring valid ID and driving license (for drivers)</li>
              <li>Wear comfortable clothing and closed-toe shoes</li>
              <li>Follow all instructions from our professional guides</li>
              <li>In case of emergency, contact our support team immediately</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Safety;
