import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './BookingForm.css';

const BookingForm = () => {
  const { carId } = useParams();
  const navigate = useNavigate();
  const [car, setCar] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const [formData, setFormData] = useState({
    startDate: '',
    startTime: '',
    duration: '60min',
    numberOfRiders: 1,
    pickupLocation: '',
    contactNumber: '',
    name: '',
    email: ''
  });

  useEffect(() => {
    fetchCar();
  }, [carId]);

  const fetchCar = async () => {
    try {
      const response = await axios.get(`/api/cars/${carId}`);
      setCar(response.data);
    } catch (error) {
      console.error('Error fetching car:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const calculateTotal = () => {
    if (!car) return 0;
    if (formData.duration === '30min' && car.price30min) {
      return car.price30min;
    } else if (formData.duration === '60min' && car.price60min) {
      return car.price60min;
    }
    return car.pricePerDay;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSubmitting(true);

    try {
      // Create booking via API
      const bookingData = {
        carId,
        startDate: formData.startDate,
        endDate: formData.startDate, // Same day for hourly bookings
        pickupLocation: formData.pickupLocation,
        contactNumber: formData.contactNumber
      };

      const response = await axios.post('/api/bookings', bookingData);

      // Also send WhatsApp message
      const whatsappMessage = `Hello! I want to book:\n\nVehicle: ${car.name}\nDate: ${formData.startDate}\nTime: ${formData.startTime}\nDuration: ${formData.duration}\nNumber of Riders: ${formData.numberOfRiders}\nName: ${formData.name}\nEmail: ${formData.email}\nPhone: ${formData.contactNumber}\nTotal: ${calculateTotal()} ${car.currency || 'AED'}`;
      const whatsappUrl = `https://wa.me/971501234567?text=${encodeURIComponent(whatsappMessage)}`;

      setSuccess(true);

      // Open WhatsApp in new tab
      window.open(whatsappUrl, '_blank');

      setTimeout(() => {
        navigate('/my-bookings');
      }, 2000);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to create booking');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!car) {
    return <div className="container">Car not found</div>;
  }

  const totalDays = formData.startDate && formData.endDate
    ? Math.ceil((new Date(formData.endDate) - new Date(formData.startDate)) / (1000 * 60 * 60 * 24))
    : 0;

  return (
    <div className="booking-form-page">
      <div className="container">
        <h1>Book Your Car</h1>
        {success && (
          <div className="alert alert-success">
            Booking created successfully! Redirecting to your bookings...
          </div>
        )}
        {error && <div className="alert alert-error">{error}</div>}

        <div className="booking-content">
          <div className="car-summary">
            <img src={car.image} alt={car.name} />
            <h3>{car.name}</h3>
            <p>{car.brand} {car.model} ({car.year})</p>
            <div className="price-info">
              {car.price60min && (
                <p className="price">60min: {car.price60min} {car.currency || 'AED'}</p>
              )}
              {car.price30min && (
                <p className="price">30min: {car.price30min} {car.currency || 'AED'}</p>
              )}
              {!car.price30min && !car.price60min && (
                <p className="price">{car.pricePerDay} {car.currency || 'AED'} / day</p>
              )}
            </div>
          </div>

          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-group">
              <label>Your Name *</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Enter your full name"
                required
              />
            </div>

            <div className="form-group">
              <label>Email Address *</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
                required
              />
            </div>

            <div className="form-group">
              <label>Contact Number *</label>
              <input
                type="tel"
                name="contactNumber"
                value={formData.contactNumber}
                onChange={handleChange}
                placeholder="+971 50 123 4567"
                required
              />
            </div>

            <div className="form-group">
              <label>Date *</label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleChange}
                min={new Date().toISOString().split('T')[0]}
                required
              />
            </div>

            <div className="form-group">
              <label>Time *</label>
              <input
                type="time"
                name="startTime"
                value={formData.startTime}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Duration *</label>
              <select
                name="duration"
                value={formData.duration}
                onChange={handleChange}
                required
              >
                {car.price30min && <option value="30min">30 Minutes</option>}
                {car.price60min && <option value="60min">60 Minutes</option>}
                {!car.price30min && !car.price60min && <option value="full-day">Full Day</option>}
              </select>
            </div>

            <div className="form-group">
              <label>Number of Riders *</label>
              <input
                type="number"
                name="numberOfRiders"
                value={formData.numberOfRiders}
                onChange={handleChange}
                min="1"
                max={car.seats}
                required
              />
              <small>Maximum {car.seats} riders for this vehicle</small>
            </div>

            <div className="form-group">
              <label>Pickup Location</label>
              <input
                type="text"
                name="pickupLocation"
                value={formData.pickupLocation}
                onChange={handleChange}
                placeholder="Sharjah, UAE (or specify location)"
              />
            </div>

            {totalDays > 0 && (
              <div className="booking-summary">
                <div className="summary-row">
                  <span>Total Days:</span>
                  <span>{totalDays}</span>
                </div>
                <div className="summary-row">
                  <span>Price per Day:</span>
                  <span>${car.pricePerDay}</span>
                </div>
                <div className="summary-row total">
                  <span>Total Price:</span>
                  <span>${calculateTotal()}</span>
                </div>
              </div>
            )}

            <button
              type="submit"
              className="btn btn-primary btn-large"
              disabled={submitting || !formData.startDate || !formData.endDate}
            >
              {submitting ? 'Booking...' : 'Confirm Booking'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BookingForm;
